import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Table from 'react-bootstrap/Table';
import Form from 'react-bootstrap/Form'
import RowData from './RowData';
import { useState } from 'react';

function App() {
  const data = [{ id: 1, firstName: "Purna Chandra", lastName: "Kanneganti", userName: "PurnaKanneganti" },
  { id: 2, firstName: "Purush", lastName: "Guda", userName: "PurushGuda" },
  { id: 3, firstName: "Naga", lastName: "Mahadeva", userName: "NagaMahadeva" },
  { id: 4, firstName: "Sampath", lastName: "Chipiri", userName: "SampathChipiri" }]

  const [data1, setData1] =  useState(data);

  // const [first, setfirst] = useState(second);
  // console.log(data1);
  const onFirstNameClick = (event) => {
    console.log(event.target.value)
    console.log(event.target.id)
    const filtered = data.filter(user => user.firstName.toString().toLocaleLowerCase().startsWith(event.target.value.toString().toLocaleLowerCase()));
    console.log(filtered)
    setData1(filtered);
  }

  return (
    <div className="App">
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Username</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td> <Form.Control type="text" id="num" controlId="num" /></td>
            <td><Form.Control type="text" id="firstName" controlId="firstName" onChange={onFirstNameClick}/></td>
            <td><Form.Control type="text" id="lastName" controlId="lastName" /></td>
            <td><Form.Control type="text" id="userName" controlId="userName" /></td>
          </tr>
          {data1.map(user => <RowData id={user.id} firstName={user.firstName} lastName = {user.lastName} userName={user.userName}/>)}
          {/* <tr>
            <td>1</td>
            <td>Mark</td>
            <td>Otto</td>
            <td>@mdo</td>
          </tr>
          <tr>
            <td>2</td>
            <td>Jacob</td>
            <td>Thornton</td>
            <td>@fat</td>
          </tr>
          <tr>
            <td>3</td>
            <td>Larry the Bird</td>
            <td>Bird</td>
            <td>@twitter</td>
          </tr> */}
        </tbody>
      </Table>
    </div>
  );
}

export default App;
